import { getConnection } from "./../database/database";

// Obtener todas las categoras
const getCategorias = async (req, res) => {
    try {
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM categoria");
        if (result.length === 0) return res.status(404).json({ message: "No se encontraron categoras." });
        res.json(result);
    } catch (error) {
        res.status(500).send(error.message);
    }
};

// Obtener una categora por su ID
const getCategoria = async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM categoria WHERE idcategoria = ?", [id]);
        if (result.length === 0) return res.status(404).json({ message: "Categora no encontrada." });
        res.json(result[0]);
    } catch (error) {
        res.status(500).send(error.message);
    }
};

// Agregar una nueva categora
const addCategoria = async (req, res) => {
    try {
        const { nombre, descripcion } = req.body;

        if (!nombre || !descripcion) {
            return res.status(400).json({ message: "Bad Request. Please fill all fields." });
        }

        const connection = await getConnection();
        await connection.query('INSERT INTO categoria (nombre, descripcion) VALUES (?, ?)', [nombre, descripcion]);
        res.json({ message: "Categora Registrada Correctamente" });

    } catch (error) {
        res.status(500).send(error.message);
    }
};

// Actualizar una categora existente
const updateCategoria = async (req, res) => {
    try {
        const { id, nombre, descripcion } = req.body;

        if (!nombre || !descripcion) {
            return res.status(400).json({ message: "Bad Request. Please fill all fields." });
        }

        const connection = await getConnection();
        const result = await connection.query('UPDATE categoria SET nombre = ?, descripcion = ? WHERE idcategoria = ?', [nombre, descripcion, id]);
        if (result.affectedRows === 0) return res.status(404).json({ message: "Categora no encontrada." });
        res.json({ message: "Categora Actualizada Correctamente" });
    } catch (error) {
        res.status(500).send(error.message);
    }
};

// Eliminar una categora
const delCategoria = async (req, res) => {
    try {
        const { id } = req.body;
        const connection = await getConnection();
        const result = await connection.query('DELETE FROM categoria WHERE idcategoria = ?', [id]);
        if (result.affectedRows === 0) return res.status(404).json({ message: "Categora no encontrada." });
        res.json({ message: "Categora Eliminada Correctamente" });
    } catch (error) {
        res.status(500).send(error.message);
    }
};

export const methods = {
    getCategorias,
    getCategoria,
    addCategoria,
    updateCategoria,
    delCategoria
};

